$(document).ready(function(){$('[data-alert-target="invoicSendMessage"]').click(function(n){n.preventDefault();let e=Swal.mixin({customClass:{confirmButton:"btn btn-success m-1",cancelButton:"btn btn-danger m-1"},buttonsStyling:!1});e.fire({title:"Are you sure?",text:"You want to sent this Invoice!",icon:"warning",showCancelButton:!0,confirmButtonText:"Yes, sent it!",cancelButtonText:"No, cancel!",reverseButtons:!0}).then(n=>{n.value?e.fire("Sent!","Invoice sent successfully.","success"):n.dismiss===Swal.DismissReason.cancel&&e.fire("Cancelled","Your imaginary file is safe :)","error")})})}),$(document).ready(function(){$(".printBTN").on("click",function(){$(".invoice-container").print({globalStyles:!0,iframe:!1})})});